import React, { useState } from "react";

function Inputvalues() 
{
    const [textEntered,setTextEntered]=useState("");
    const [assignValue,setAssinVlaue]=useState("");

    const textEnteredValue=(event)=>
        {
            setTextEntered(event.target.value);
        }
        const enterValue=()=>
            {
                setAssinVlaue(textEntered)
            }
   return(
    <div className="text-center p-5">
        <h3>Enter your Name:</h3>
        <div>
            <input placeholder="enter your name" onChange={textEnteredValue}/>
            <button className="btn btn-sm btn-outline-primary" onClick={enterValue}>Enter</button>
        </div>
        <div>
            <p className="mt-5">Your entered name is {assignValue}</p>
        </div>
    </div>

   ) 
}
export default Inputvalues
